(7, 13, 4, 'Bác sĩ Chuyên khoa II', 30, 'Giám đốc chuyên môn và quản lý điều hành - Phòng khám đa khoa MSC (2021 - Nay)', 'Hà Nội', NULL, 500000, '<p>B&aacute;c sĩ Chuy&ecirc;n khoa II Trần Trọng Thắng</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Gần 30 năm kinh nghiệm lĩnh vực Cơ xương khớp</p>\r\n	</li>\r\n	<li>\r\n	<p>Từng c&ocirc;ng t&aacute;c tại Bệnh viện Xanh P&ocirc;n, Bệnh viện Phục hồi chức năng H&agrave; Nội</p>\r\n	</li>\r\n	<li>\r\n	<p>B&aacute;c sĩ nhận kh&aacute;m tất cả c&aacute;c độ tuổi</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kh&aacute;m v&agrave; điều trị</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Kh&aacute;m v&agrave; điều trị bệnh tho&aacute;i h&oacute;a khớp</p>\r\n	</li>\r\n	<li>\r\n	<p>Chấn thương trong thể thao, sinh hoạt, tai nạn...</p>\r\n	</li>\r\n	<li>\r\n	<p>Bệnh l&yacute; cột sống: tho&aacute;t vị địa đệm, tho&aacute;i h&oacute;a cột sống..</p>\r\n	</li>\r\n	<li>\r\n	<p>Vi&ecirc;m bao quang khớp: vi&ecirc;m g&acirc;n, bao hoạt dịch, d&acirc;y chằng....</p>\r\n	</li>\r\n	<li>\r\n	<p>Hồi phục sau phẫu thuật cơ xương khớp</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>Qu&aacute; tr&igrave;nh c&ocirc;ng t&aacute;c</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Gi&aacute;m đốc chuy&ecirc;n m&ocirc;n v&agrave; quản l&yacute; điều h&agrave;nh -&nbsp;Ph&ograve;ng kh&aacute;m đa khoa MSC (2021 - Nay)</p>\r\n	</li>\r\n	<li>\r\n	<p>Ph&oacute; gi&aacute;m đốc v&agrave; quản l&yacute; điều h&agrave;nh -&nbsp;Bệnh viện Phục hồi chức năng H&agrave; Nội (2011 - 2021)</p>\r\n	</li>\r\n	<li>\r\n	<p>B&aacute;c sĩ - Bệnh viện Xanh P&ocirc;n (1995 - 2011)</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>Qu&aacute; tr&igrave;nh đ&agrave;o tạo</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Định hướng chuy&ecirc;n khoa chuy&ecirc;n ng&agrave;nh T&acirc;m Thần - Bộ Y Tế (2017)</p>\r\n	</li>\r\n	<li>\r\n	<p>Kh&oacute;a học quản l&yacute; bệnh viện - Bộ Y Tế (2015)</p>\r\n	</li>\r\n	<li>\r\n	<p>Chuy&ecirc;n khoa II Cơ Xương Khớp - Đại học Y H&agrave; Nội (2012 - 2014)</p>\r\n	</li>\r\n	<li>\r\n	<p>Đa khoa -&nbsp; Đại học Y H&agrave; Nội (1990 - 1995)</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>S&aacute;ch v&agrave; c&aacute;c c&ocirc;ng tr&igrave;nh nghi&ecirc;n cứu, b&aacute;o c&aacute;o khoa học</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Đề t&agrave;i cơ sở đ&aacute;nh gi&aacute; kết quả điều trị vi&ecirc;m điểm b&aacute;m g&acirc;n bằng s&oacute;ng xung k&iacute;ch (2014)</p>\r\n	</li>\r\n	<li>\r\n	<p>Đề t&agrave;i cấp Bộ về chế tạo v&agrave; ứng dụng b&agrave;n tập phục hồi chức năng chi dưới trong điều trị g&atilde;y m&acirc;m ch&agrave;y (2013)</p>\r\n	</li>\r\n	<li>\r\n	<p>Đề t&agrave;i cơ sở đ&aacute;nh gi&aacute; kết quả phục hồi chức năng sau mổ xơ cơ Delta (2006)</p>\r\n	</li>\r\n	<li>\r\n	<p>Đề t&agrave;i cơ sở đ&aacute;nh gi&aacute; kết quả phẫu thuật xơ cơ detal tại bệnh viện Xanh P&ocirc;n (2005)</p>\r\n	</li>\r\n	<li>\r\n	<p>Tham gia đề t&agrave;i cấp th&agrave;nh phố: ứng dụng đinh đ&agrave;n hồi Metaizeau trong điều trị g&atilde;y xương d&agrave;i (2000)</p>\r\n	</li>\r\n</ul>', '2024-11-27 04:44:19', '2024-11-27 04:44:19'),
(8, 12, 4, 'ThS.BS', 20, 'Phó trưởng khoa Cơ Xương Khớp Bệnh viện E', 'Hà Nội', 15, 500000, '<p>ThS.BS Nguyễn Trần Trung</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>B&aacute;c sĩ c&oacute; nhiều năm kinh nghiệm trong kh&aacute;m v&agrave; điều trị Cơ xương khớp</p>\r\n	</li>\r\n	<li>\r\n	<p>Ph&oacute; trưởng khoa Cơ Xương Khớp Bệnh viện E</p>\r\n	</li>\r\n	<li>\r\n	<p>B&aacute;c sĩ nhận kh&aacute;m từ 15 tuổi trở l&ecirc;n</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhận kh&aacute;m v&agrave; điều trị</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Vi&ecirc;m cột sống d&iacute;nh khớp:&nbsp;Vi&ecirc;m khớp c&ugrave;ng chậu, vi&ecirc;m khớp ngoại vi, đau v&agrave; sưng ở khớp h&aacute;ng v&agrave; khớp gối</p>\r\n	</li>\r\n	<li>\r\n	<p>Vi&ecirc;m khớp dạng thấp:&nbsp;Khớp sưng, n&oacute;ng, đỏ, đau dai dẳng. C&aacute;c dấu hiệu n&agrave;y hầu hết xuất hiện đối xứng ở hai b&ecirc;n cơ thể (hai b&ecirc;n tay, cổ tay, đầu gối,...)</p>\r\n	</li>\r\n	<li>\r\n	<p>Tho&aacute;i khớp gối, khớp vai:&nbsp;Kh&ocirc; khớp, cứng khớp, lục khục, mất ngủ, teo cơ, sưng tấy,&nbsp;Đau, giảm tầm vận động, hạn chế di chuyển của khớp</p>\r\n	</li>\r\n	<li>\r\n	<p>Tho&aacute;i h&oacute;a cột sống thắt lưng</p>\r\n	</li>\r\n	<li>\r\n	<p>Tho&aacute;t vị đĩa đệm</p>\r\n	</li>\r\n	<li>\r\n	<p>Lo&atilde;ng xương</p>\r\n	</li>\r\n	<li>\r\n	<p>G&uacute;t</p>\r\n	</li>\r\n	<li>\r\n	<p>Vi&ecirc;m khớp</p>\r\n	</li>\r\n	<li>\r\n	<p>Si&ecirc;u &acirc;m đ&aacute;nh gi&aacute; c&aacute;c tổn thương cơ xương khớp</p>\r\n	</li>\r\n	<li>\r\n	<p>Thực hiện c&aacute;c thủ thuật điều trị: Ti&ecirc;m khớp, ti&ecirc;m khớp dưới hướng dẫn si&ecirc;u &acirc;m, ti&ecirc;m huyết tương gi&agrave;u tiểu cầu,...</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong>Qu&aacute; tr&igrave;nh c&ocirc;ng t&aacute;c</strong></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><strong>B&aacute;c sĩ chuy&ecirc;n khoa Cơ Xương Khớp,&nbsp;Ph&ograve;ng kh&aacute;m Đa khoa MSC (Nay)</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Ph&oacute; Trưởng Khoa Cơ Xương Khớp,&nbsp;Bệnh viện E (2019 - nay)</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>B&aacute;c sĩ Điều trị tại Khoa Cơ Xương Khớp,&nbsp;Bệnh viện E (2009 - 2019)</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Giảng vi&ecirc;n thỉnh giảng Bộ m&ocirc;n Nội Cơ Xương Khớp,&nbsp;Trường Đại học Y Dược Quốc Gia (2009 - 2019)</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Giảng vi&ecirc;n c&aacute;c lớp đ&agrave;o tạo li&ecirc;n tục cho b&aacute;c sĩ sau đại học, Bệnh viện E (2009 - 2019)</strong></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong>Qu&aacute; tr&igrave;nh đ&agrave;o tạo</strong></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><strong>Cao học chuy&ecirc;n ng&agrave;nh Nội khoa,&nbsp;Đại học Y H&agrave; Nội (11/2013 - 12/2015)</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Đại học chuy&ecirc;n ng&agrave;nh B&aacute;c sĩ đa khoa,&nbsp;Học viện Qu&acirc;n Y (07/2003 - 07/2009)</strong></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong>Chứng chỉ trong nước hoặc nước ngo&agrave;i</strong></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><strong>Sư phạm y học cơ bản cho giảng vi&ecirc;n đ&agrave;o tạo li&ecirc;n tục,&nbsp;Trung t&acirc;m HMDP, Tổng hội y học Việt Nam (9/2022)</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Tham gia giảng dạy kh&oacute;a học: &ldquo;Chẩn đo&aacute;n v&agrave; điều trị bệnh l&yacute; cơ xương khớp v&agrave; ti&ecirc;m khớp ngoại vi&rdquo;,&nbsp;Bệnh viện E (6/2017)</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Định hướng chuy&ecirc;n ng&agrave;nh Cơ Xương Khớp,&nbsp;Bệnh viện Bạch Mai (2012)</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Đ&agrave;o tạo li&ecirc;n tục Phương ph&aacute;p nghi&ecirc;n cứu khoa học d&agrave;nh cho b&aacute;c sĩ,&nbsp;Bệnh viện E (11/2011)</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Si&ecirc;u &acirc;m tổng qu&aacute;t,&nbsp;Đại học Y H&agrave; Nội (2011)</strong></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong>S&aacute;ch v&agrave; c&aacute;c c&ocirc;ng tr&igrave;nh b&aacute;o c&aacute;o khoa học</strong></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><strong>Giảm vi&ecirc;m đa khớp gốc chi: Ca l&acirc;m s&agrave;ng &raquo; (Mai Thị Minh T&acirc;m, Nguyễn Trần Trung, Trịnh Việt Anh, Vũ Hồng Anh), trang 171, Tạp ch&iacute; Y học Việt Nam tập 502 (Th&aacute;ng 5/2021)</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Đ&aacute;nh gi&aacute; t&aacute;c dụng điều trị tho&aacute;i h&oacute;a khớp gối nguy&ecirc;n ph&aacute;t của liệu ph&aacute;p ti&ecirc;m MD-Collagen: Nghi&ecirc;n cứu quan s&aacute;t, một trung t&acirc;m &raquo; (Đặng Hồng Hoa, Nguyễn Trần Trung, Đặng Ch&iacute; Hiếu) (Th&aacute;ng 8/2020)</strong></p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Đ&aacute;nh gi&aacute; kết quả sau 8 tuần điều trị bằng liệu ph&aacute;p huyết tương gi&agrave;u tiểu cầu tự th&acirc;n ở bệnh nh&acirc;n vi&ecirc;m lồi cầu ngo&agrave;i xương c&aacute;nh tay &raquo;. (Nguyễn Trần Trung, Đặng Hồng Hoa), trang 68, Y học thực h&agrave;nh ISSN (Số 4/2016)</strong></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong>Th&agrave;nh vi&ecirc;n c&aacute;c Hội khoa học, tổ chức chuy&ecirc;n m&ocirc;n</strong></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Tham gia c&aacute;c lớp đ&agrave;o tạo quốc tế tại Ph&aacute;p, Nhật, Th&aacute;i Lan, Singapore, Việt Nam</p>\r\n	</li>\r\n	<li>\r\n	<p>Th&agrave;nh vi&ecirc;n Hội Thấp Khớp học Việt Nam</p>\r\n	</li>\r\n	<li>\r\n	<p>Th&agrave;nh vi&ecirc;n Hội Thấp Khớp học H&agrave; Nội</p>\r\n	</li>\r\n	<li>\r\n	<p>Th&agrave;nh vi&ecirc;n hội thấp khớp học Ch&acirc;u &Aacute; Th&aacute;i b&igrave;nh dương (APLAR)</p>\r\n	</li>\r\n</ul>', '2024-11-27 04:48:04', '2024-11-27 04:48:04'),
(9, 11, 4, 'Phó Giáo sư, Tiến sĩ', 25, 'Trưởng khoa Phẫu thuật Cột sống, Bệnh viện Việt Đức', 'Hà Nội', 7, 500000, '<p>Ph&oacute; Gi&aacute;o sư, Tiến sĩ Đinh Ngọc Sơn</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>B&aacute;c sĩ c&oacute; 25 năm kinh nghiệm về bệnh l&yacute; li&ecirc;n quan cột sống</p>\r\n	</li>\r\n	<li>\r\n	<p>Hiện l&agrave; Trưởng khoa Phẫu thuật Cột sống, Bệnh viện Việt Đức</p>\r\n	</li>\r\n	<li>\r\n	<p>B&aacute;c sĩ nhận kh&aacute;m từ 7 tuổi trở l&ecirc;n&nbsp;</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>Kh&aacute;m v&agrave; điều trị</p>\r\n\r\n<p><strong>Bệnh l&yacute; cột sống:</strong></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Tho&aacute;i h&oacute;a cột sống</p>\r\n	</li>\r\n	<li>\r\n	<p>Tho&aacute;t vị đĩa đệm</p>\r\n	</li>\r\n	<li>\r\n	<p>Thay đổi đĩa đệm theo tuổi</p>\r\n	</li>\r\n	<li>\r\n	<p>Tho&aacute;i h&oacute;a đĩa đệm</p>\r\n	</li>\r\n	<li>\r\n	<p>R&aacute;ch v&ograve;m sợi</p>\r\n	</li>\r\n	<li>\r\n	<p>Lồi đĩa đệm</p>\r\n	</li>\r\n	<li>\r\n	<p>Tho&aacute;i h&oacute;a mỏm khớp</p>\r\n	</li>\r\n	<li>\r\n	<p>Hẹp ống sống</p>\r\n	</li>\r\n	<li>\r\n	<p>Trượt đốt sống</p>\r\n	</li>\r\n	<li>\r\n	<p>Cong vẹo cột sống</p>\r\n	</li>\r\n	<li>\r\n	<p>Gai cột sống</p>\r\n	</li>\r\n	<li>\r\n	<p>Đau cổ vai g&aacute;y, t&ecirc; b&igrave; 2 c&aacute;nh tay</p>\r\n	</li>\r\n	<li>\r\n	<p>Đau thắt lưng</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong>Chấn thương, chỉnh h&igrave;nh</strong></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Chấn thương cột sống cổ</p>\r\n	</li>\r\n	<li>\r\n	<p>Chấn thương cột sống ngực - thắt lưng</p>\r\n	</li>\r\n	<li>\r\n	<p>Chấn thương cột sống v&ugrave;ng c&ugrave;ng cụt</p>\r\n	</li>\r\n	<li>\r\n	<p>Tổn thương tủy sống m&agrave; kh&ocirc;ng c&oacute; bất thường XQ</p>\r\n	</li>\r\n	<li>\r\n	<p>G&atilde;y cột sống nhiều tầng</p>\r\n	</li>\r\n	<li>\r\n	<p>Tổn thương xuy&ecirc;n thấu cột sống</p>\r\n	</li>\r\n	<li>\r\n	<p>Chỉnh h&igrave;nh cột sống (vẹo cột sống, g&ugrave; cột sống)</p>\r\n	</li>\r\n	<li>\r\n	<p>Nội soi cột sống cổ, cột sống thắt lưng điều trị tho&aacute;t vị đĩa đệm</p>\r\n	</li>\r\n	<li>\r\n	<p>Phẫu thuật cột sống &iacute;t x&acirc;m lấn (MIS, XLIP, c&aacute;c can thiệp tối thiểu như tạo h&igrave;nh nh&acirc;n nh&agrave;y bằng song cao tần&hellip;)</p>\r\n	</li>\r\n	<li>\r\n	<p>Bơm xi-măng v&agrave;o th&acirc;n đốt sống</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong>C&aacute;c bệnh l&yacute; về cột sống Thần kinh sọ n&atilde;o Bệnh l&yacute; Cơ Xương khớp:&nbsp;</strong></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Điều trị, phẫu thuật chấn thương cột sống, tủy sống</p>\r\n	</li>\r\n	<li>\r\n	<p>Kh&aacute;m, điều trị, phẫu thuật tho&aacute;t vị đĩa đệm cột sống cổ v&agrave; thắt lưng Kh&aacute;m, điều trị,</p>\r\n	</li>\r\n	<li>\r\n	<p>Phẫu thuật vết thương v&agrave; chấn thương d&acirc;y thần kinh ngoại vi</p>\r\n	</li>\r\n	<li>\r\n	<p>Kh&aacute;m, điều trị Hội chứng tiền đ&igrave;nh</p>\r\n	</li>\r\n	<li>\r\n	<p>Điều trị c&aacute;c bệnh vi&ecirc;m, tho&aacute;i h&oacute;a cột sống v&agrave; xương khớp</p>\r\n	</li>\r\n	<li>\r\n	<p>Phẫu thuật tạo h&igrave;nh&nbsp;cột sống&nbsp;</p>\r\n	</li>\r\n	<li>\r\n	<p>Phẫu thuật u tủy sống</p>\r\n	</li>\r\n	<li>\r\n	<p>C&aacute;c bệnh l&yacute; li&ecirc;n quan đến tủy sống v&agrave; thần kinh ngoại bi&ecirc;n</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>Qu&aacute; tr&igrave;nh đ&agrave;o tạo&nbsp;</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Bảo vệ luận &aacute;n Tiến sỹ, Đại học Y H&agrave; Nội (2014)&nbsp;</p>\r\n	</li>\r\n	<li>\r\n	<p>Tốt nghiệp B&aacute;c sĩ nội tr&uacute;, Đại học Y H&agrave; Nội (2003)</p>\r\n	</li>\r\n	<li>\r\n	<p>Tốt nghiệp B&aacute;c sĩ, Đại học Y H&agrave; Nội (1998)</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>Đ&agrave;o tạo nước ngo&agrave;i</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Tham gia nhiều kho&aacute; học ngắn hạn về cập nhật c&aacute;c kỹ thuật mới trong phẫu thuật cột sống tại Ch&acirc;u &Acirc;u như: chỉnh h&igrave;nh cột sống, phẫu thuật đường b&ecirc;n (XLIP), Phẫu thuật &iacute;t x&acirc;m lấn (MIS), phẫu thuật thay đĩa đệm nh&acirc;n tạo cột sống cổ v&agrave; thắt lưng (2007- 2016)</p>\r\n	</li>\r\