INSERT INTO `packages` (`id`, `specialty_id`, `hospital_name`, `description`, `image`, `address`, `price`, `created_at`, `updated_at`) VALUES
(1, 15, 'Gói khám sức khỏe tổng quát cơ bản cho nam (PKYD1M)', '<p><strong>G&oacute;i kh&aacute;m bao gồm:</strong></p>\r\n\r\n<ul>\r\n	<li>Kh&aacute;m l&acirc;m s&agrave;ng</li>\r\n	<li>X&eacute;t nghiệm m&aacute;u</li>\r\n	<li>X&eacute;t nghiệm chức năng gan, thận</li>\r\n	<li>X&eacute;t nghiệm chức năng chuyển h&oacute;a</li>\r\n	<li>Điện tim</li>\r\n	<li>Si&ecirc;u &acirc;m ổ bụng</li>\r\n	<li>Chụp X-quang tim phổi</li>\r\n</ul>\r\n\r\n<p>G&oacute;i kh&aacute;m tại Ph&ograve;ng kh&aacute;m Bệnh viện Đại học Y dược 1. G&oacute;i kh&aacute;m được chia th&agrave;nh c&aacute;c danh mục cho nam giới tr&ecirc;n 50 tuổi v&agrave; nam giới dưới 50 tuổi.</p>\r\n\r\n<h2>Danh mục G&oacute;i</h2>', '1732546162jpg', 'Phòng khám Bệnh viện Đại học Y dược 1 - 20-22 Dương Quang Trung, Phường 12, Quận 10, Tp. HCM', 2000000.00, '2024-11-25 14:49:22', '2024-11-27 08:18:33'),
(2, 15, 'Tầm soát bệnh tiêu chuẩn cho nam và nữ (DC2)', '<p>Tầm so&aacute;t bệnh ti&ecirc;u chuẩn cho nam v&agrave; nữ (DC2)</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Chỉ với 60 ph&uacute;t, bạn sẽ được sẽ được đ&aacute;nh gi&aacute; sức khỏe ban đầu th&ocirc;ng qua bộ c&acirc;u hỏi s&agrave;ng lọc, đo chỉ số BMI v&agrave; thăm kh&aacute;m c&ugrave;ng b&aacute;c sĩ nội khoa (15 ph&uacute;t). Thực hiện c&aacute;c cận l&acirc;m s&agrave;ng đ&aacute;nh gi&aacute; l&ecirc;n tới 10 bệnh l&yacute; phổ biến ở nam v&agrave; nữ (30 ph&uacute;t). Cuối c&ugrave;ng, bạn sẽ được b&aacute;c sĩ tư vấn kết quả v&agrave; kế hoạch sống thọ (15 ph&uacute;t), gi&uacute;p bạn bảo vệ sức khỏe d&agrave;i l&acirc;u.</p>\r\n	</li>\r\n	<li>\r\n	<p>G&oacute;i kh&aacute;m được thực hiện tại Trung T&acirc;m Tầm So&aacute;t Bệnh Doctor Check</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>Trung T&acirc;m Kh&aacute;m Sức Khoẻ Tổng Qu&aacute;t Doctor Check</p>\r\n\r\n<p><strong>Trung T&acirc;m Tầm So&aacute;t Bệnh Doctor Check</strong>&nbsp;l&agrave; đơn vị cung cấp dịch vụ kh&aacute;m tổng qu&aacute;t uy t&iacute;n, nhanh ch&oacute;ng, tiện lợi v&agrave; nhận được sự t&iacute;n nhiệm của nhiều Kh&aacute;ch H&agrave;ng đến kh&aacute;m.&nbsp;</p>\r\n\r\n<p><strong>5 l&yacute; do tại sao n&ecirc;n tầm so&aacute;t tại Doctor Check</strong></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>&nbsp;Đội ngũ b&aacute;c sĩ gi&agrave;u kinh nghiệm đến từ c&aacute;c bệnh viện lớn tại TPHCM sẽ thăm kh&aacute;m v&agrave; hướng dẫn c&aacute;ch sống khỏe v&agrave; sống thọ hơn</p>\r\n	</li>\r\n	<li>\r\n	<p>Hệ thống m&aacute;y m&oacute;c hiện đại đến từ c&aacute;c h&atilde;ng đa Quốc gia như Mỹ, Nhật Bản, Đức,... gi&uacute;p n&acirc;ng cao tỷ lệ chẩn đo&aacute;n ch&iacute;nh x&aacute;c v&agrave; ph&aacute;t hiện bất thường</p>\r\n	</li>\r\n	<li>\r\n	<p>Quy tr&igrave;nh thăm kh&aacute;m nhanh ch&oacute;ng chỉ từ 60 - 90 ph&uacute;t gi&uacute;p kh&aacute;ch h&agrave;ng tiết kiệm thời gian</p>\r\n	</li>\r\n	<li>\r\n	<p>Ph&ograve;ng kh&aacute;m mở cửa sớm từ 6h - 15h gi&uacute;p kh&aacute;ch h&agrave;ng c&oacute; thể kh&aacute;m v&agrave; về ngay trong ng&agrave;y</p>\r\n	</li>\r\n	<li>\r\n	<p>20 c&ocirc;ng ty bảo hiểm được Doctor Check hỗ trợ thanh to&aacute;n trực tiếp</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong>Thời gian nhận kết quả:</strong></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Sau khi ho&agrave;n tất quy tr&igrave;nh kh&aacute;m, kh&aacute;ch h&agrave;ng sẽ nhận được kết quả giấy trong v&ograve;ng 15 ph&uacute;t.</p>\r\n	</li>\r\n	<li>\r\n	<p>C&agrave;i App Doctor Check Member gi&uacute;p Qu&yacute; kh&aacute;ch h&agrave;ng sẽ nhận được kết quả tức th&igrave;, đồng thời theo d&otilde;i được t&igrave;nh trạng sức khoẻ của m&igrave;nh qua từng năm.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong>H&igrave;nh thức nhận kết quả</strong>:&nbsp;Kh&aacute;ch h&agrave;ng c&oacute; thể lựa chọn một trong hai h&igrave;nh thức trả kết quả sau đ&acirc;y:</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>C&aacute;ch 1: B&aacute;c sĩ tư vấn v&agrave; trả kết quả trực tiếp tại ph&ograve;ng kh&aacute;m.</p>\r\n	</li>\r\n	<li>\r\n	<p>C&aacute;ch 2: B&aacute;c sĩ tư vấn kết quả bằng h&igrave;nh thức gọi điện thoại. Hồ sơ bệnh &aacute;n được chuyển về tận nh&agrave; Qu&yacute; kh&aacute;ch h&agrave;ng.</p>\r\n	</li>\r\n</ul>', '1732695462png', 'Doctor Check - Tầm Soát Bệnh Để Sống Thọ Hơn 429 Tô Hiến Thành, Phường 14, Quận 10, Tp. Hồ Chí Minh', 1950000.00, '2024-11-27 08:17:42', '2024-11-27 08:17:42'),
